        me.isSnakeTail = true;
        me.stroke = "transparent"
        me.onDeath = function() {
            if (this.isSnakeTail) { //wake up tail mobs
                for (let i = 0; i < mob.length; i++) {
                    if (mob[i].isSnakeTail && mob[i].alive) {
                        mob[i].isSnakeTail = false;
                        mob[i].do = mob[i].doActive
                        mob[i].removeConsBB();
                    }
                }
            }
        };
